import { useEffect, useRef } from 'react';

const WaveBackground = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d', { alpha: true });
    // Add extra width for shadow effects and wave extension
    const extraWidth = 100;
    const canvasWidth = canvas.width = window.innerWidth + extraWidth * 2;
    const height = canvas.height = window.innerHeight * 0.5;

    // Use viewport width for drawing logic
    const width = window.innerWidth;

    // Translate context to center the drawing with padding for shadows
    ctx.translate(extraWidth, 0);

    // Particle class with wave motion
    class Particle {
      constructor() {
        this.x = Math.random() * width;
        this.y = Math.random() * height;
        this.vx = (Math.random() - 0.5) * 0.5;
        this.size = Math.random() * 2 + 1;
        this.life = Math.random() * 100;
        this.opacity = Math.random() * 0.5 + 0.3;
      }

      update() {
        this.x += this.vx;
        this.life += 0.5;
        
        // Key wave formula - makes particles flow in wave pattern
        this.y += Math.sin(this.x * 0.01 + this.life * 0.02) * 0.3;

        // Wrap around edges
        if (this.x < 0) this.x = width;
        if (this.x > width) this.x = 0;
        if (this.y < 0) this.y = height;
        if (this.y > height) this.y = 0;
      }

      draw() {
        ctx.beginPath();
        ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
        ctx.fillStyle = `rgba(255, 255, 255, ${this.opacity})`;
        ctx.fill();
      }
    }

    // Flowing ribbon wave class - like silk or fluid
    class WaveRibbon {
      constructor(baseY, amplitude, speed, opacity, slope, thickness) {
        this.baseY = baseY;
        this.amplitude = amplitude;
        this.speed = speed;
        this.opacity = opacity;
        this.slope = slope;
        this.thickness = thickness;
        this.offset = Math.random() * Math.PI * 2;
        this.secondaryOffset = Math.random() * Math.PI;
      }

      update() {
        this.offset += this.speed;
        this.secondaryOffset += this.speed * 0.7;
      }

      draw() {
        // Calculate path for smooth ribbon
        const topPath = [];
        const bottomPath = [];

        // Extend beyond canvas width to prevent cutoff at edges
        // Extra extension accounts for amplitude, thickness, slope, and shadow effects
        for (let x = -200; x <= width + 200; x += 3) {
          // Diagonal angle
          const angleOffset = (x - width / 2) * this.slope;

          // Layered wave motion - fluid dynamics
          const primaryWave = Math.sin(x * 0.008 + this.offset) * this.amplitude;
          const secondaryWave = Math.sin(x * 0.015 - this.secondaryOffset) * (this.amplitude * 0.4);
          const tertiaryWave = Math.cos(x * 0.012 + this.offset * 0.8) * (this.amplitude * 0.25);

          const centerY = this.baseY + angleOffset + primaryWave + secondaryWave + tertiaryWave;

          topPath.push({ x, y: centerY - this.thickness / 2 });
          bottomPath.push({ x, y: centerY + this.thickness / 2 });
        }

        // Create gradient for silk-like translucent effect - more transparent
        const gradient = ctx.createLinearGradient(0, this.baseY - this.thickness, 0, this.baseY + this.thickness);
        gradient.addColorStop(0, `rgba(255, 255, 255, ${this.opacity * 0.25})`);
        gradient.addColorStop(0.5, `rgba(255, 255, 255, ${this.opacity * 0.95})`);
        gradient.addColorStop(1, `rgba(255, 255, 255, ${this.opacity * 0.25})`);

        // Draw filled ribbon shape - fluid motion
        ctx.save();
        ctx.beginPath();
        topPath.forEach((point, i) => {
          if (i === 0) ctx.moveTo(point.x, point.y);
          else ctx.lineTo(point.x, point.y);
        });
        bottomPath.reverse().forEach(point => ctx.lineTo(point.x, point.y));
        ctx.closePath();

        // Soft outer glow - energy waves (more transparent)
        ctx.shadowBlur = 35;
        ctx.shadowColor = `rgba(255, 255, 255, ${this.opacity * 0.55})`;
        ctx.fillStyle = gradient;
        ctx.fill();

        // Bright edge highlight - like light catching silk (more transparent)
        ctx.shadowBlur = 20;
        ctx.strokeStyle = `rgba(255, 255, 255, ${this.opacity * 0.75})`;
        ctx.lineWidth = 2;
        ctx.beginPath();
        topPath.forEach((point, i) => {
          if (i === 0) ctx.moveTo(point.x, point.y);
          else ctx.lineTo(point.x, point.y);
        });
        ctx.stroke();

        ctx.restore();
      }
    }

    // Create particles
    const particles = [];
    const particleCount = 100;
    for (let i = 0; i < particleCount; i++) {
      particles.push(new Particle());
    }

    // Create flowing ribbon waves - minimal count, maximum impact
    const waveRibbons = [];
    const ribbonCount = 3; // Just 3 ribbons for elegance
    const baseSlope = -0.15; // Negative slope - left low, right high
    for (let i = 0; i < ribbonCount; i++) {
      waveRibbons.push(new WaveRibbon(
        height * 0.35 + (i - 1) * 30,   // baseY - moved up (was 0.5)
        20 + i * 6,                      // amplitude - smooth curves
        0.008 + i * 0.005,               // speed - varied rhythm
        0.28 - i * 0.05,                 // opacity - more transparent
        baseSlope - i * 0.018,           // slope - reversed angle (negative)
        60 + i * 18                      // thickness - very wide! (60-96px)
      ));
    }

    // Draw connections between close particles
    function drawConnections() {
      for (let i = 0; i < particles.length; i++) {
        for (let j = i + 1; j < particles.length; j++) {
          const dx = particles[i].x - particles[j].x;
          const dy = particles[i].y - particles[j].y;
          const distance = Math.sqrt(dx * dx + dy * dy);

          // Draw line if particles are close enough
          if (distance < 100) {
            ctx.beginPath();
            ctx.strokeStyle = `rgba(255, 255, 255, ${0.1 * (1 - distance / 100)})`;
            ctx.lineWidth = 0.5;
            ctx.moveTo(particles[i].x, particles[i].y);
            ctx.lineTo(particles[j].x, particles[j].y);
            ctx.stroke();
          }
        }
      }
    }

    // Animation loop
    function animate() {
      // Clear canvas
      ctx.clearRect(0, 0, width, height);

      // Update and draw flowing ribbons - transformation and continuity
      waveRibbons.forEach(ribbon => {
        ribbon.update();
        ribbon.draw();
      });

      // Update and draw particles
      particles.forEach(particle => {
        particle.update();
        particle.draw();
      });

      // Draw particle connections
      drawConnections();

      // Loop at 60 FPS
      requestAnimationFrame(animate);
    }

    animate();

    // Handle resize
    const handleResize = () => {
      canvas.width = window.innerWidth + extraWidth * 2;
      canvas.height = window.innerHeight * 0.5;
      ctx.setTransform(1, 0, 0, 1, 0, 0); // Reset transform
      ctx.translate(extraWidth, 0); // Reapply translation
    };
    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return (
    <canvas
      ref={canvasRef}
      style={{
        position: 'absolute',
        top: '50%',
        left: 0,
        width: '100%',
        height: '50%',
        zIndex: 0,
        pointerEvents: 'none'
      }}
    />
  );
};

export default WaveBackground;
