import { useEffect, useRef } from 'react';

const WaveBackground = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    const width = window.innerWidth;
    const height = window.innerHeight * 0.5;
    
    canvas.width = width;
    canvas.height = height;

    // XMB-style wave configuration
    const waves = [];
    const waveCount = 6;

    for (let i = 0; i < waveCount; i++) {
      waves.push({
        y: height * 0.5,
        length: 0.015 + i * 0.002,
        amplitude: 80 + i * 20,
        frequency: 0.01 + i * 0.005,
        speed: 0.3 + i * 0.1,
        opacity: 0.15 - i * 0.02,
        offset: i * Math.PI / 3
      });
    }

    let time = 0;

    const animate = () => {
      requestAnimationFrame(animate);
      
      // Clear with fade effect for smooth trails
      ctx.fillStyle = 'rgba(0, 0, 0, 0.05)';
      ctx.fillRect(0, 0, width, height);
      
      time += 0.015;

      waves.forEach((wave, index) => {
        ctx.beginPath();
        ctx.strokeStyle = `rgba(255, 255, 255, ${wave.opacity})`;
        ctx.lineWidth = 3 + index * 0.5;
        ctx.shadowBlur = 20;
        ctx.shadowColor = 'rgba(255, 255, 255, 0.5)';

        // Draw smooth wave
        for (let x = 0; x < width; x += 2) {
          const y = wave.y + 
                   Math.sin(x * wave.length + time * wave.speed + wave.offset) * wave.amplitude +
                   Math.sin(x * wave.length * 2 - time * wave.speed * 0.7) * (wave.amplitude * 0.3) +
                   Math.cos(x * wave.length * 0.5 + time * wave.speed * 0.5 + wave.offset) * (wave.amplitude * 0.5);

          if (x === 0) {
            ctx.moveTo(x, y);
          } else {
            ctx.lineTo(x, y);
          }
        }

        ctx.stroke();
        ctx.shadowBlur = 0;
      });
    };

    animate();

    // Handle resize
    const handleResize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight * 0.5;
    };
    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return (
    <canvas
      ref={canvasRef}
      style={{
        position: 'absolute',
        top: '50%',
        left: 0,
        width: '100%',
        height: '50%',
        zIndex: 0,
        pointerEvents: 'none',
        filter: 'blur(1px)'
      }}
    />
  );
};

export default WaveBackground;
