import { useEffect, useRef } from 'react';
import * as THREE from 'three';

const WaveBackground = () => {
  const containerRef = useRef(null);

  useEffect(() => {
    const container = containerRef.current;
    if (!container) return;

    // Scene setup
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(
      75,
      window.innerWidth / (window.innerHeight * 0.5),
      0.1,
      1000
    );
    camera.position.set(0, 0, 30);
    camera.lookAt(0, 0, 0);

    const renderer = new THREE.WebGLRenderer({ 
      alpha: true, 
      antialias: true
    });
    const height = window.innerHeight * 0.5;
    renderer.setSize(window.innerWidth, height);
    renderer.setClearColor(0x000000, 0);
    container.appendChild(renderer.domElement);

    // Create smooth flowing wave ribbons with dense particles
    const waveRibbons = [];
    const ribbonCount = 5;

    for (let ribbon = 0; ribbon < ribbonCount; ribbon++) {
      // Dense particles for smooth ribbons
      const particlesPerRibbon = 1000;
      const positions = new Float32Array(particlesPerRibbon * 3);
      const sizes = new Float32Array(particlesPerRibbon);
      const colors = new Float32Array(particlesPerRibbon * 3);

      // Create smooth wave ribbon
      for (let i = 0; i < particlesPerRibbon; i++) {
        const progress = i / particlesPerRibbon;
        const x = (progress - 0.5) * 120;
        
        // Smooth wave curves
        const baseWave = Math.sin(progress * Math.PI * 3 + ribbon * 0.8) * 6;
        const flow = Math.sin(progress * Math.PI * 6 + ribbon * 0.4) * 2.5;
        const y = baseWave + flow;
        
        // Layered depth
        const z = -ribbon * 2.5 - 4;
        
        positions[i * 3] = x;
        positions[i * 3 + 1] = y;
        positions[i * 3 + 2] = z;
        
        // Larger size for smooth appearance
        const edgeFade = Math.sin(progress * Math.PI);
        sizes[i] = (2.0 + Math.random() * 0.5) * Math.pow(edgeFade, 0.5);
        
        // Smooth gradient
        const brightness = 0.95 - (ribbon * 0.15);
        const centerGlow = Math.pow(edgeFade, 0.7);
        const finalBrightness = brightness * centerGlow;
        
        colors[i * 3] = finalBrightness;
        colors[i * 3 + 1] = finalBrightness;
        colors[i * 3 + 2] = finalBrightness;
      }

      const geometry = new THREE.BufferGeometry();
      geometry.setAttribute('position', new THREE.BufferAttribute(positions, 3));
      geometry.setAttribute('size', new THREE.BufferAttribute(sizes, 1));
      geometry.setAttribute('color', new THREE.BufferAttribute(colors, 3));

      // Smooth material with soft glow
      const material = new THREE.PointsMaterial({
        size: 2.5,
        vertexColors: true,
        transparent: true,
        opacity: 0.75 - (ribbon * 0.1),
        blending: THREE.AdditiveBlending,
        sizeAttenuation: true,
        depthWrite: false
      });

      const particles = new THREE.Points(geometry, material);
      scene.add(particles);

      waveRibbons.push({
        particles,
        geometry,
        positions,
        colors,
        sizes,
        ribbon,
        phase: ribbon * Math.PI / 2.5,
        speed: 0.12 + ribbon * 0.03
      });
    }

    // Slow, flowing animation
    let time = 0;
    const animate = () => {
      requestAnimationFrame(animate);
      time += 0.008; // Slow motion

      waveRibbons.forEach((ribbon) => {
        const positions = ribbon.positions;
        const colors = ribbon.colors;
        const particleCount = positions.length / 3;
        
        for (let i = 0; i < particleCount; i++) {
          const progress = i / particleCount;
          const x = (progress - 0.5) * 100;
          
          // Flowing wave motion - slow and elegant
          const wave1 = Math.sin(progress * Math.PI * 4 + ribbon.ribbon + time * ribbon.speed) * 5;
          const wave2 = Math.sin(progress * Math.PI * 8 + ribbon.ribbon * 0.5 - time * ribbon.speed * 0.7) * 2;
          const wave3 = Math.cos(progress * Math.PI * 6 + time * ribbon.speed * 0.5 + ribbon.phase) * 1.5;
          
          const y = wave1 + wave2 + wave3;
          
          positions[i * 3] = x + Math.sin(time * 0.2 + ribbon.phase) * 3;
          positions[i * 3 + 1] = y;
          
          // Slow depth oscillation
          const depthOscillation = Math.sin(time * 0.3 + ribbon.phase) * 0.5;
          positions[i * 3 + 2] = -ribbon.ribbon * 3 - 5 + depthOscillation;
          
          // Dynamic brightness based on wave height
          const heightBrightness = (Math.abs(wave1) / 5) * 0.3 + 0.7;
          const baseBrightness = 1.0 - (ribbon.ribbon * 0.13);
          const pulseBrightness = Math.sin(time * 0.4 + ribbon.phase) * 0.1 + 0.9;
          const finalBrightness = baseBrightness * heightBrightness * pulseBrightness;
          
          colors[i * 3] = finalBrightness;
          colors[i * 3 + 1] = finalBrightness;
          colors[i * 3 + 2] = finalBrightness;
        }
        
        ribbon.geometry.attributes.position.needsUpdate = true;
        ribbon.geometry.attributes.color.needsUpdate = true;
      });

      renderer.render(scene, camera);
    };

    animate();

    // Handle resize
    const handleResize = () => {
      const height = window.innerHeight * 0.45;
      camera.aspect = window.innerWidth / height;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, height);
    };
    window.addEventListener('resize', handleResize);

    // Cleanup
    return () => {
      window.removeEventListener('resize', handleResize);
      if (container && renderer.domElement) {
        container.removeChild(renderer.domElement);
      }
      waveRibbons.forEach(ribbon => {
        ribbon.geometry.dispose();
        ribbon.particles.material.dispose();
      });
      renderer.dispose();
    };
  }, []);

  return (
    <div
      ref={containerRef}
      style={{
        position: 'absolute',
        top: '50%',
        left: 0,
        width: '100%',
        height: '50%',
        zIndex: 0,
        pointerEvents: 'none',
        overflow: 'hidden'
      }}
    />
  );
};

export default WaveBackground;
