import React, { useState } from 'react';
import './App.css';

function App() {
  const [selectedRole, setSelectedRole] = useState('student');
  const [isSignUp, setIsSignUp] = useState(false);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    name: '',
    studentId: ''
  });

  const roleColors = {
    student: 'radial-gradient(ellipse at center, #1a5c1a 0%, #0d3d0d 50%, #051a05 100%)',
    teacher: 'radial-gradient(ellipse at center, #5c1a1a 0%, #3d0d0d 50%, #1a0505 100%)',
    admin: 'radial-gradient(ellipse at center, #1a4d5c 0%, #0d2d3d 50%, #05141a 100%)'
  };

  const handleRoleChange = (role) => {
    setSelectedRole(role);
    setIsSignUp(false);
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isSignUp) {
      console.log('Sign Up Data:', formData);
      alert(`Student Sign Up Successful!\nName: ${formData.name}\nEmail: ${formData.email}`);
    } else {
      console.log('Login Data:', { role: selectedRole, ...formData });
      alert(`Login as ${selectedRole.toUpperCase()}\nEmail: ${formData.email}`);
    }
    // Reset form
    setFormData({ email: '', password: '', name: '', studentId: '' });
  };

  const handleForgotPassword = () => {
    if (formData.email) {
      alert(`Password reset link has been sent to:\n${formData.email}`);
    } else {
      alert('Please enter your email address first to reset your password.');
    }
  };

  return (
    <div className="app" style={{ background: roleColors[selectedRole] }}>
      <div className="wave-background">
        {/* Particles/Sparkles */}
        <div className="particles">
          {[...Array(50)].map((_, i) => (
            <div key={i} className="particle" style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              animationDelay: `${Math.random() * 5}s`,
              animationDuration: `${3 + Math.random() * 4}s`
            }}></div>
          ))}
        </div>
        
        {/* Curved wave lines at bottom */}
        <div className="silk-waves">
          <svg className="wave-curves" viewBox="0 0 1440 250" preserveAspectRatio="none">
            <defs>
              {/* Pure white - brightest */}
              <linearGradient id="wave-grad-bright" x1="0%" y1="0%" x2="100%" y2="0%">
                <stop offset="0%" style={{stopColor: 'rgba(255,255,255,0.2)', stopOpacity: 1}} />
                <stop offset="50%" style={{stopColor: 'rgba(255,255,255,0.7)', stopOpacity: 1}} />
                <stop offset="100%" style={{stopColor: 'rgba(255,255,255,0.2)', stopOpacity: 1}} />
              </linearGradient>
              {/* Light gray-white */}
              <linearGradient id="wave-grad-light" x1="0%" y1="0%" x2="100%" y2="0%">
                <stop offset="0%" style={{stopColor: 'rgba(230,230,230,0.15)', stopOpacity: 1}} />
                <stop offset="50%" style={{stopColor: 'rgba(240,240,240,0.5)', stopOpacity: 1}} />
                <stop offset="100%" style={{stopColor: 'rgba(230,230,230,0.15)', stopOpacity: 1}} />
              </linearGradient>
              {/* Medium gray-white */}
              <linearGradient id="wave-grad-medium" x1="0%" y1="0%" x2="100%" y2="0%">
                <stop offset="0%" style={{stopColor: 'rgba(200,200,200,0.12)', stopOpacity: 1}} />
                <stop offset="50%" style={{stopColor: 'rgba(220,220,220,0.4)', stopOpacity: 1}} />
                <stop offset="100%" style={{stopColor: 'rgba(200,200,200,0.12)', stopOpacity: 1}} />
              </linearGradient>
              {/* Dark gray-white */}
              <linearGradient id="wave-grad-dark" x1="0%" y1="0%" x2="100%" y2="0%">
                <stop offset="0%" style={{stopColor: 'rgba(180,180,180,0.08)', stopOpacity: 1}} />
                <stop offset="50%" style={{stopColor: 'rgba(200,200,200,0.28)', stopOpacity: 1}} />
                <stop offset="100%" style={{stopColor: 'rgba(180,180,180,0.08)', stopOpacity: 1}} />
              </linearGradient>
              <filter id="wave-blur">
                <feGaussianBlur stdDeviation="4" />
              </filter>
            </defs>
            
            {/* Smooth flowing angled curves */}
            <path className="wave-line wave-1" fill="none" stroke="url(#wave-grad-bright)" strokeWidth="6" filter="url(#wave-blur)"
              d="M-200,180 C-50,165 50,195 200,175 C350,155 450,185 600,170 C750,155 850,180 1000,165 C1150,150 1250,175 1400,160 C1550,145 1650,170 1800,155" />
            
            <path className="wave-line wave-2" fill="none" stroke="url(#wave-grad-bright)" strokeWidth="7" filter="url(#wave-blur)"
              d="M-200,140 C-50,155 50,125 200,145 C350,165 450,135 600,150 C750,165 850,140 1000,155 C1150,170 1250,145 1400,160 C1550,175 1650,150 1800,165" />
            
            {/* Light gray-white curves */}
            <path className="wave-line wave-3" fill="none" stroke="url(#wave-grad-light)" strokeWidth="5" filter="url(#wave-blur)"
              d="M-200,120 C-50,135 50,105 200,120 C350,135 450,115 600,130 C750,145 850,125 1000,140 C1150,155 1250,135 1400,150 C1550,165 1650,145 1800,160" />
            
            <path className="wave-line wave-4" fill="none" stroke="url(#wave-grad-light)" strokeWidth="5.5" filter="url(#wave-blur)"
              d="M-200,160 C-50,145 50,175 200,155 C350,135 450,165 600,150 C750,135 850,160 1000,145 C1150,130 1250,155 1400,140 C1550,125 1650,150 1800,135" />
            
            <path className="wave-line wave-5" fill="none" stroke="url(#wave-grad-light)" strokeWidth="4.5" filter="url(#wave-blur)"
              d="M-200,95 C-50,110 50,80 200,95 C350,110 450,90 600,105 C750,120 850,100 1000,115 C1150,130 1250,110 1400,125 C1550,140 1650,120 1800,135" />
            
            {/* Medium gray-white curves */}
            <path className="wave-line wave-6" fill="none" stroke="url(#wave-grad-medium)" strokeWidth="4" filter="url(#wave-blur)"
              d="M-200,110 C-50,125 50,95 200,110 C350,125 450,105 600,120 C750,135 850,115 1000,130 C1150,145 1250,125 1400,140 C1550,155 1650,135 1800,150" />
            
            <path className="wave-line wave-7" fill="none" stroke="url(#wave-grad-medium)" strokeWidth="4.8" filter="url(#wave-blur)"
              d="M-200,150 C-50,135 50,165 200,145 C350,125 450,155 600,140 C750,125 850,150 1000,135 C1150,120 1250,145 1400,130 C1550,115 1650,140 1800,125" />
            
            <path className="wave-line wave-8" fill="none" stroke="url(#wave-grad-medium)" strokeWidth="5" filter="url(#wave-blur)"
              d="M-200,170 C-50,155 50,185 200,165 C350,145 450,175 600,160 C750,145 850,170 1000,155 C1150,140 1250,165 1400,150 C1550,135 1650,160 1800,145" />
            
            {/* Dark gray-white curves */}
            <path className="wave-line wave-9" fill="none" stroke="url(#wave-grad-dark)" strokeWidth="3.5" filter="url(#wave-blur)"
              d="M-200,105 C-50,120 50,90 200,105 C350,120 450,100 600,115 C750,130 850,110 1000,125 C1150,140 1250,120 1400,135 C1550,150 1650,130 1800,145" />
            
            <path className="wave-line wave-10" fill="none" stroke="url(#wave-grad-dark)" strokeWidth="4" filter="url(#wave-blur)"
              d="M-200,130 C-50,145 50,115 200,130 C350,145 450,125 600,140 C750,155 850,135 1000,150 C1150,165 1250,145 1400,160 C1550,175 1650,155 1800,170" />
            
            <path className="wave-line wave-11" fill="none" stroke="url(#wave-grad-dark)" strokeWidth="4.2" filter="url(#wave-blur)"
              d="M-200,155 C-50,140 50,170 200,150 C350,130 450,160 600,145 C750,130 850,155 1000,140 C1150,125 1250,150 1400,135 C1550,120 1650,145 1800,130" />
            
            <path className="wave-line wave-12" fill="none" stroke="url(#wave-grad-dark)" strokeWidth="3.8" filter="url(#wave-blur)"
              d="M-200,190 C-50,175 50,205 200,185 C350,165 450,195 600,180 C750,165 850,190 1000,175 C1150,160 1250,185 1400,170 C1550,155 1650,180 1800,165" />
            
            {/* Additional flowing curves */}
            <path className="wave-line wave-13" fill="none" stroke="url(#wave-grad-bright)" strokeWidth="5.5" filter="url(#wave-blur)"
              d="M-200,85 C-50,100 50,70 200,85 C350,100 450,80 600,95 C750,110 850,90 1000,105 C1150,120 1250,100 1400,115 C1550,130 1650,110 1800,125" />
          </svg>
        </div>
      </div>
      <div className="main-title">
        <div className="title-icon">ðŸ“š</div>
        <div className="title-text-large">Library Duty</div>
        <div className="title-text-small">Assignment System</div>
      </div>
      
      <div className="login-container">
        <div className="login-box">
          {!isSignUp && (
            <div className="role-selector">
              <button
                className={`role-btn ${selectedRole === 'student' ? 'active' : ''}`}
                onClick={() => handleRoleChange('student')}
                style={{ 
                  background: selectedRole === 'student' ? roleColors.student : '#f0f0f0',
                  color: selectedRole === 'student' ? 'white' : '#333'
                }}
              >
                Student
              </button>
              <button
                className={`role-btn ${selectedRole === 'teacher' ? 'active' : ''}`}
                onClick={() => handleRoleChange('teacher')}
                style={{ 
                  background: selectedRole === 'teacher' ? roleColors.teacher : '#f0f0f0',
                  color: selectedRole === 'teacher' ? 'white' : '#333'
                }}
              >
                Teacher
              </button>
              <button
                className={`role-btn ${selectedRole === 'admin' ? 'active' : ''}`}
                onClick={() => handleRoleChange('admin')}
                style={{ 
                  background: selectedRole === 'admin' ? roleColors.admin : '#f0f0f0',
                  color: selectedRole === 'admin' ? 'white' : '#333'
                }}
              >
                Admin
              </button>
            </div>
          )}

          <div className="form-container">
            <h2 className="form-title">
              {isSignUp ? 'Student Sign Up' : `${selectedRole.charAt(0).toUpperCase() + selectedRole.slice(1)} Login`}
            </h2>
            
            <form onSubmit={handleSubmit}>
              {isSignUp && (
                <>
                  <div className="form-group">
                    <label htmlFor="name">Full Name</label>
                    <input
                      type="text"
                      id="name"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      placeholder="Enter your full name"
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="studentId">Student ID</label>
                    <input
                      type="text"
                      id="studentId"
                      name="studentId"
                      value={formData.studentId}
                      onChange={handleInputChange}
                      placeholder="Enter your student ID"
                      required
                    />
                  </div>
                </>
              )}
              
              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  placeholder="Enter your email"
                  required
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="password">Password</label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  value={formData.password}
                  onChange={handleInputChange}
                  placeholder="Enter your password"
                  required
                />
              </div>
              
              <button 
                type="submit" 
                className="submit-btn"
                style={{ background: roleColors[selectedRole] }}
              >
                {isSignUp ? 'Sign Up' : 'Login'}
              </button>
            </form>

            {!isSignUp && (
              <div className="forgot-password">
                <button 
                  className="forgot-password-btn"
                  onClick={handleForgotPassword}
                >
                  Forgot Password?
                </button>
              </div>
            )}

            {selectedRole === 'student' && (
              <div className="toggle-form">
                <p>
                  {isSignUp ? 'Already have an account?' : "Don't have an account?"}
                  <button 
                    className="toggle-btn"
                    onClick={() => setIsSignUp(!isSignUp)}
                  >
                    {isSignUp ? 'Login' : 'Sign Up'}
                  </button>
                </p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
