import { useEffect, useRef } from 'react';

const WaveBackground = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    const width = window.innerWidth;
    const height = window.innerHeight * 0.5;
    
    canvas.width = width;
    canvas.height = height;

    // XMB-style wave configuration - concentrated vertically with angle
    const waves = [];
    const waveCount = 6;
    const angleSlope = 0.08; // Diagonal angle (positive = upward from left to right)

    for (let i = 0; i < waveCount; i++) {
      waves.push({
        y: height * 0.5,
        length: 0.015 + i * 0.002,
        amplitude: 30 + i * 8,  // Reduced amplitude - more focused
        frequency: 0.01 + i * 0.005,
        speed: 0.3 + i * 0.1,
        opacity: 0.15 - i * 0.02,
        offset: i * Math.PI / 3,
        slope: angleSlope + (i * 0.01) // Slight variation per wave
      });
    }

    let time = 0;

    const animate = () => {
      requestAnimationFrame(animate);
      
      // Clear canvas completely - transparent background
      ctx.clearRect(0, 0, width, height);
      
      time += 0.015;

      waves.forEach((wave, index) => {
        // Calculate top wave path
        const topPath = [];
        const bottomPath = [];
        const thickness = 40 + index * 10; // Ribbon thickness
        
        for (let x = 0; x < width; x += 3) {
          // Add diagonal slope for angled waves
          const angleOffset = (x - width / 2) * wave.slope;
          
          const baseY = wave.y + angleOffset +
                       Math.sin(x * wave.length + time * wave.speed + wave.offset) * wave.amplitude +
                       Math.sin(x * wave.length * 2 - time * wave.speed * 0.7) * (wave.amplitude * 0.25) +
                       Math.cos(x * wave.length * 0.5 + time * wave.speed * 0.5 + wave.offset) * (wave.amplitude * 0.3);
          
          topPath.push({ x, y: baseY - thickness / 2 });
          bottomPath.push({ x, y: baseY + thickness / 2 });
        }

        // Create gradient for ribbon depth
        const gradient = ctx.createLinearGradient(0, wave.y - thickness, 0, wave.y + thickness);
        gradient.addColorStop(0, `rgba(255, 255, 255, ${wave.opacity * 0.3})`);
        gradient.addColorStop(0.5, `rgba(255, 255, 255, ${wave.opacity * 0.8})`);
        gradient.addColorStop(1, `rgba(255, 255, 255, ${wave.opacity * 0.3})`);

        // Draw filled ribbon shape
        ctx.beginPath();
        topPath.forEach((point, i) => {
          if (i === 0) ctx.moveTo(point.x, point.y);
          else ctx.lineTo(point.x, point.y);
        });
        bottomPath.reverse().forEach((point) => {
          ctx.lineTo(point.x, point.y);
        });
        ctx.closePath();
        
        // Add glow
        ctx.shadowBlur = 30;
        ctx.shadowColor = 'rgba(255, 255, 255, 0.6)';
        ctx.fillStyle = gradient;
        ctx.fill();

        // Add bright edge highlights
        ctx.shadowBlur = 15;
        ctx.shadowColor = 'rgba(255, 255, 255, 0.8)';
        ctx.strokeStyle = `rgba(255, 255, 255, ${wave.opacity * 0.6})`;
        ctx.lineWidth = 2;
        
        ctx.beginPath();
        topPath.forEach((point, i) => {
          if (i === 0) ctx.moveTo(point.x, point.y);
          else ctx.lineTo(point.x, point.y);
        });
        ctx.stroke();

        ctx.shadowBlur = 0;
      });
    };

    animate();

    // Handle resize
    const handleResize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight * 0.5;
    };
    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return (
    <canvas
      ref={canvasRef}
      style={{
        position: 'absolute',
        top: '50%',
        left: 0,
        width: '100%',
        height: '50%',
        zIndex: 0,
        pointerEvents: 'none',
        filter: 'blur(1px)'
      }}
    />
  );
};

export default WaveBackground;
