import { useEffect, useRef } from 'react';
import * as THREE from 'three';

const WaveBackground = () => {
  const containerRef = useRef(null);

  useEffect(() => {
    const container = containerRef.current;
    if (!container) return;

    // Scene setup
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(
      75,
      window.innerWidth / (window.innerHeight * 0.5),
      0.1,
      1000
    );
    camera.position.set(0, 4, 12);
    camera.lookAt(0, -2, 0);

    const renderer = new THREE.WebGLRenderer({ 
      alpha: true, 
      antialias: true,
      powerPreference: "high-performance"
    });
    const height = window.innerHeight * 0.5;
    renderer.setSize(window.innerWidth, height);
    renderer.setClearColor(0x000000, 0);
    container.appendChild(renderer.domElement);

    // Create multiple wave planes with different materials
    const waves = [];
    const waveCount = 10;

    for (let i = 0; i < waveCount; i++) {
      const geometry = new THREE.PlaneGeometry(20, 5, 150, 30);
      
      // Different shades of white/gray
      const colors = [
        0xffffff, // pure white
        0xf0f0f0, // light gray
        0xe8e8e8, // medium-light gray
        0xd0d0d0, // medium gray
        0xffffff, // pure white
        0xf5f5f5, // off-white
        0xe0e0e0, // light-medium gray
        0xc8c8c8, // medium-dark gray
        0xffffff, // pure white
        0xd8d8d8  // gray
      ];

      const material = new THREE.MeshStandardMaterial({
        color: colors[i],
        transparent: true,
        opacity: 0.4 + (i * 0.04),
        side: THREE.DoubleSide,
        metalness: 0.3,
        roughness: 0.4,
        emissive: colors[i],
        emissiveIntensity: 0.1
      });

      const mesh = new THREE.Mesh(geometry, material);
      
      // Position waves spread across the view
      mesh.position.z = -i * 0.5 - 2;
      mesh.position.y = -0.5 + (i * 0.2) - Math.sin(i) * 0.3;
      mesh.position.x = Math.sin(i * 0.5) * 2;
      mesh.rotation.x = Math.PI / 4;
      mesh.rotation.z = -0.2;
      
      waves.push({
        mesh,
        geometry,
        speed: 0.0008 + i * 0.0003,
        amplitude: 0.2 + i * 0.03,
        frequency: 0.4 + i * 0.08,
        offset: i * Math.PI / 5
      });
      
      scene.add(mesh);
    }

    // Lighting
    const ambientLight = new THREE.AmbientLight(0xffffff, 1);
    scene.add(ambientLight);

    const directionalLight1 = new THREE.DirectionalLight(0xffffff, 0.8);
    directionalLight1.position.set(5, 5, 5);
    scene.add(directionalLight1);

    const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.5);
    directionalLight2.position.set(-5, 3, 2);
    scene.add(directionalLight2);

    const pointLight = new THREE.PointLight(0xffffff, 0.6);
    pointLight.position.set(0, 4, 4);
    scene.add(pointLight);

    // Animation
    let time = 0;
    const animate = () => {
      requestAnimationFrame(animate);
      time += 0.01;

      waves.forEach((wave, index) => {
        const positions = wave.geometry.attributes.position;
        
        for (let i = 0; i < positions.count; i++) {
          const x = positions.getX(i);
          const y = positions.getY(i);
          
          // Create flowing wave motion with more amplitude
          const waveX = Math.sin(x * wave.frequency + time * wave.speed * 12 + wave.offset) * wave.amplitude;
          const waveY = Math.cos(y * wave.frequency * 0.6 + time * wave.speed * 10 + wave.offset) * wave.amplitude * 0.6;
          const waveZ = Math.sin((x + y) * wave.frequency * 0.3 + time * wave.speed * 8) * wave.amplitude * 0.3;
          
          positions.setZ(i, waveX + waveY + waveZ);
        }
        
        positions.needsUpdate = true;
        
        // Gentle rotation and movement for organic flow
        wave.mesh.rotation.y = Math.sin(time * wave.speed * 6 + wave.offset) * 0.15;
        wave.mesh.position.x = Math.sin(index * 0.5) * 2 + Math.sin(time * wave.speed * 4 + wave.offset) * 0.5;
        wave.mesh.position.y = -0.5 + (index * 0.2) - Math.sin(index) * 0.3 + Math.cos(time * wave.speed * 3 + wave.offset) * 0.2;
      });

      renderer.render(scene, camera);
    };

    animate();

    // Handle resize
    const handleResize = () => {
      const height = window.innerHeight * 0.45;
      camera.aspect = window.innerWidth / height;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, height);
    };
    window.addEventListener('resize', handleResize);

    // Cleanup
    return () => {
      window.removeEventListener('resize', handleResize);
      if (container && renderer.domElement) {
        container.removeChild(renderer.domElement);
      }
      waves.forEach(wave => {
        wave.geometry.dispose();
        wave.mesh.material.dispose();
      });
      renderer.dispose();
    };
  }, []);

  return (
    <div
      ref={containerRef}
      style={{
        position: 'absolute',
        top: '55%',
        left: 0,
        width: '100%',
        height: '45%',
        zIndex: 0,
        pointerEvents: 'none',
        overflow: 'hidden'
      }}
    />
  );
};

export default WaveBackground;
