import { useEffect, useRef } from 'react';
import * as THREE from 'three';

const WaveBackground = () => {
  const containerRef = useRef(null);

  useEffect(() => {
    const container = containerRef.current;
    if (!container) return;

    // Scene setup with perspective
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(
      70,
      window.innerWidth / (window.innerHeight * 0.5),
      0.1,
      1000
    );
    camera.position.set(0, 8, 15);
    camera.lookAt(0, 0, 0);

    const renderer = new THREE.WebGLRenderer({ 
      alpha: true, 
      antialias: true,
      powerPreference: "high-performance"
    });
    const height = window.innerHeight * 0.5;
    renderer.setSize(window.innerWidth, height);
    renderer.setClearColor(0x000000, 0);
    container.appendChild(renderer.domElement);

    // Create silk fabric ribbons with dramatic curves
    const silkRibbons = [];
    const ribbonCount = 5;

    for (let i = 0; i < ribbonCount; i++) {
      // High-resolution geometry for smooth silk curves
      const geometry = new THREE.PlaneGeometry(40, 6, 300, 60);
      
      // Silk-like material with depth
      const brightness = 1.0 - (i * 0.12);
      const silkColor = new THREE.Color(brightness, brightness, brightness);

      const material = new THREE.MeshStandardMaterial({
        color: silkColor,
        transparent: true,
        opacity: 0.7 - (i * 0.1),
        side: THREE.DoubleSide,
        metalness: 0.2,
        roughness: 0.3,
        emissive: silkColor,
        emissiveIntensity: 0.4,
        flatShading: false
      });

      const mesh = new THREE.Mesh(geometry, material);
      
      // Position with dramatic depth
      mesh.position.z = -i * 2.5 - 3;
      mesh.position.y = -1 + (i * 0.5);
      mesh.rotation.x = Math.PI / 3;
      
      silkRibbons.push({
        mesh,
        geometry,
        baseY: -1 + (i * 0.5),
        speed: 0.8 + i * 0.2,
        amplitude: 1.2 + i * 0.3,
        offset: i * Math.PI / 2.5,
        layer: i
      });
      
      scene.add(mesh);
    }

    // Silk fabric lighting - dramatic shadows and highlights
    const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
    scene.add(ambientLight);

    // Top key light
    const keyLight = new THREE.DirectionalLight(0xffffff, 1.8);
    keyLight.position.set(0, 10, 8);
    scene.add(keyLight);

    // Strong back light for silk glow
    const backLight = new THREE.DirectionalLight(0xccddff, 1.2);
    backLight.position.set(0, -8, -8);
    scene.add(backLight);

    // Side rim lights for 3D definition
    const rimLight1 = new THREE.PointLight(0xffffff, 2.0);
    rimLight1.position.set(-15, 5, 10);
    scene.add(rimLight1);

    const rimLight2 = new THREE.PointLight(0xffffff, 2.0);
    rimLight2.position.set(15, 5, 10);
    scene.add(rimLight2);

    // Fill light from below
    const fillLight = new THREE.PointLight(0x88bbff, 1.0);
    fillLight.position.set(0, -5, 12);
    scene.add(fillLight);

    // Silk fabric animation - flowing organic curves
    let time = 0;
    const animate = () => {
      requestAnimationFrame(animate);
      time += 0.015;

      silkRibbons.forEach((ribbon) => {
        const positions = ribbon.geometry.attributes.position;
        const normals = ribbon.geometry.attributes.normal;
        
        for (let i = 0; i < positions.count; i++) {
          const x = positions.getX(i);
          const y = positions.getY(i);
          
          // Multiple wave layers for silk-like flow
          const primaryWave = Math.sin(x * 0.25 + time * ribbon.speed) * ribbon.amplitude;
          const secondaryWave = Math.sin(x * 0.4 - time * ribbon.speed * 0.7 + ribbon.offset) * ribbon.amplitude * 0.6;
          const tertiaryWave = Math.cos(x * 0.15 + y * 0.2 + time * ribbon.speed * 0.5) * ribbon.amplitude * 0.4;
          
          // Cross waves for organic silk ripples
          const crossWave = Math.sin(y * 0.5 + time * ribbon.speed * 0.6 + ribbon.offset) * ribbon.amplitude * 0.3;
          
          // Depth undulation for 3D silk effect
          const depthCurve = Math.sin(x * 0.1 + time * ribbon.speed * 0.4 + ribbon.layer) * ribbon.amplitude * 0.5;
          
          // Combined silk motion
          const zOffset = primaryWave + secondaryWave + tertiaryWave + crossWave + depthCurve;
          positions.setZ(i, zOffset);
        }
        
        // Update geometry
        positions.needsUpdate = true;
        ribbon.geometry.computeVertexNormals();
        normals.needsUpdate = true;
        
        // Flowing horizontal motion
        ribbon.mesh.position.x = Math.sin(time * 0.4 + ribbon.offset) * 2.0;
        
        // Gentle vertical breathing
        ribbon.mesh.position.y = ribbon.baseY + Math.sin(time * 0.3 + ribbon.offset) * 0.4;
        
        // Subtle rotation for organic feel
        ribbon.mesh.rotation.z = Math.sin(time * 0.2 + ribbon.offset) * 0.05;
      });

      renderer.render(scene, camera);
    };

    animate();

    // Handle resize
    const handleResize = () => {
      const height = window.innerHeight * 0.45;
      camera.aspect = window.innerWidth / height;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, height);
    };
    window.addEventListener('resize', handleResize);

    // Cleanup
    return () => {
      window.removeEventListener('resize', handleResize);
      if (container && renderer.domElement) {
        container.removeChild(renderer.domElement);
      }
      waves.forEach(wave => {
        wave.geometry.dispose();
        wave.mesh.material.dispose();
      });
      renderer.dispose();
    };
  }, []);

  return (
    <div
      ref={containerRef}
      style={{
        position: 'absolute',
        top: '50%',
        left: 0,
        width: '100%',
        height: '50%',
        zIndex: 0,
        pointerEvents: 'none',
        overflow: 'hidden'
      }}
    />
  );
};

export default WaveBackground;
