import React, { useState } from 'react';
import './App.css';

function App() {
  const [selectedRole, setSelectedRole] = useState('student');
  const [isSignUp, setIsSignUp] = useState(false);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    name: '',
    studentId: ''
  });

  const roleColors = {
    student: 'radial-gradient(ellipse at center, #1a5c1a 0%, #0d3d0d 50%, #051a05 100%)',
    teacher: 'radial-gradient(ellipse at center, #5c1a1a 0%, #3d0d0d 50%, #1a0505 100%)',
    admin: 'radial-gradient(ellipse at center, #1a4d5c 0%, #0d2d3d 50%, #05141a 100%)'
  };

  const handleRoleChange = (role) => {
    setSelectedRole(role);
    setIsSignUp(false);
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isSignUp) {
      console.log('Sign Up Data:', formData);
      alert(`Student Sign Up Successful!\nName: ${formData.name}\nEmail: ${formData.email}`);
    } else {
      console.log('Login Data:', { role: selectedRole, ...formData });
      alert(`Login as ${selectedRole.toUpperCase()}\nEmail: ${formData.email}`);
    }
    // Reset form
    setFormData({ email: '', password: '', name: '', studentId: '' });
  };

  const handleForgotPassword = () => {
    if (formData.email) {
      alert(`Password reset link has been sent to:\n${formData.email}`);
    } else {
      alert('Please enter your email address first to reset your password.');
    }
  };

  return (
    <div className="app" style={{ background: roleColors[selectedRole] }}>
      <div className="wave-background">
        {/* Particles/Sparkles */}
        <div className="particles">
          {[...Array(50)].map((_, i) => (
            <div key={i} className="particle" style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              animationDelay: `${Math.random() * 5}s`,
              animationDuration: `${3 + Math.random() * 4}s`
            }}></div>
          ))}
        </div>
        
        {/* Professional wave patterns */}
        <svg className="waves" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1440 800" preserveAspectRatio="none">
          <defs>
            <linearGradient id="wave-gradient-1" x1="0%" y1="0%" x2="100%" y2="0%">
              <stop offset="0%" style={{stopColor: 'rgba(255,255,255,0.05)', stopOpacity: 1}} />
              <stop offset="50%" style={{stopColor: 'rgba(255,255,255,0.25)', stopOpacity: 1}} />
              <stop offset="100%" style={{stopColor: 'rgba(255,255,255,0.05)', stopOpacity: 1}} />
            </linearGradient>
            <linearGradient id="wave-gradient-2" x1="0%" y1="0%" x2="100%" y2="0%">
              <stop offset="0%" style={{stopColor: 'rgba(255,255,255,0.03)', stopOpacity: 1}} />
              <stop offset="50%" style={{stopColor: 'rgba(255,255,255,0.2)', stopOpacity: 1}} />
              <stop offset="100%" style={{stopColor: 'rgba(255,255,255,0.03)', stopOpacity: 1}} />
            </linearGradient>
            <filter id="wave-glow">
              <feGaussianBlur stdDeviation="10" result="coloredBlur"/>
              <feMerge>
                <feMergeNode in="coloredBlur"/>
                <feMergeNode in="coloredBlur"/>
                <feMergeNode in="SourceGraphic"/>
              </feMerge>
            </filter>
          </defs>
          
          {/* Repeating wave patterns - like ocean waves */}
          <path className="wave-path wave1" fill="none" stroke="url(#wave-gradient-1)" strokeWidth="12" 
            strokeLinecap="round" filter="url(#wave-glow)"
            d="M-400,350 Q-300,300 -200,350 T0,350 T200,350 T400,350 T600,350 T800,350 T1000,350 T1200,350 T1400,350 T1600,350 T1800,350 T2000,350 T2200,350" />
          
          <path className="wave-path wave2" fill="none" stroke="url(#wave-gradient-2)" strokeWidth="10" 
            strokeLinecap="round" filter="url(#wave-glow)"
            d="M-400,450 Q-300,500 -200,450 T0,450 T200,450 T400,450 T600,450 T800,450 T1000,450 T1200,450 T1400,450 T1600,450 T1800,450 T2000,450 T2200,450" />
          
          <path className="wave-path wave3" fill="none" stroke="url(#wave-gradient-1)" strokeWidth="14" 
            strokeLinecap="round" filter="url(#wave-glow)"
            d="M-400,250 Q-300,200 -200,250 T0,250 T200,250 T400,250 T600,250 T800,250 T1000,250 T1200,250 T1400,250 T1600,250 T1800,250 T2000,250 T2200,250" />
          
          <path className="wave-path wave4" fill="none" stroke="url(#wave-gradient-2)" strokeWidth="8" 
            strokeLinecap="round" filter="url(#wave-glow)"
            d="M-400,550 Q-300,600 -200,550 T0,550 T200,550 T400,550 T600,550 T800,550 T1000,550 T1200,550 T1400,550 T1600,550 T1800,550 T2000,550 T2200,550" />
            
          <path className="wave-path wave5" fill="none" stroke="url(#wave-gradient-1)" strokeWidth="11" 
            strokeLinecap="round" filter="url(#wave-glow)"
            d="M-400,180 Q-300,130 -200,180 T0,180 T200,180 T400,180 T600,180 T800,180 T1000,180 T1200,180 T1400,180 T1600,180 T1800,180 T2000,180 T2200,180" />
            
          <path className="wave-path wave6" fill="none" stroke="url(#wave-gradient-2)" strokeWidth="9" 
            strokeLinecap="round" filter="url(#wave-glow)"
            d="M-400,620 Q-300,670 -200,620 T0,620 T200,620 T400,620 T600,620 T800,620 T1000,620 T1200,620 T1400,620 T1600,620 T1800,620 T2000,620 T2200,620" />
        </svg>
      </div>
      <div className="main-title">
        <div className="title-icon">ðŸ“š</div>
        <div className="title-text-large">Library Duty</div>
        <div className="title-text-small">Assignment System</div>
      </div>
      
      <div className="login-container">
        <div className="login-box">
          {!isSignUp && (
            <div className="role-selector">
              <button
                className={`role-btn ${selectedRole === 'student' ? 'active' : ''}`}
                onClick={() => handleRoleChange('student')}
                style={{ 
                  background: selectedRole === 'student' ? roleColors.student : '#f0f0f0',
                  color: selectedRole === 'student' ? 'white' : '#333'
                }}
              >
                Student
              </button>
              <button
                className={`role-btn ${selectedRole === 'teacher' ? 'active' : ''}`}
                onClick={() => handleRoleChange('teacher')}
                style={{ 
                  background: selectedRole === 'teacher' ? roleColors.teacher : '#f0f0f0',
                  color: selectedRole === 'teacher' ? 'white' : '#333'
                }}
              >
                Teacher
              </button>
              <button
                className={`role-btn ${selectedRole === 'admin' ? 'active' : ''}`}
                onClick={() => handleRoleChange('admin')}
                style={{ 
                  background: selectedRole === 'admin' ? roleColors.admin : '#f0f0f0',
                  color: selectedRole === 'admin' ? 'white' : '#333'
                }}
              >
                Admin
              </button>
            </div>
          )}

          <div className="form-container">
            <h2 className="form-title">
              {isSignUp ? 'Student Sign Up' : `${selectedRole.charAt(0).toUpperCase() + selectedRole.slice(1)} Login`}
            </h2>
            
            <form onSubmit={handleSubmit}>
              {isSignUp && (
                <>
                  <div className="form-group">
                    <label htmlFor="name">Full Name</label>
                    <input
                      type="text"
                      id="name"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      placeholder="Enter your full name"
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="studentId">Student ID</label>
                    <input
                      type="text"
                      id="studentId"
                      name="studentId"
                      value={formData.studentId}
                      onChange={handleInputChange}
                      placeholder="Enter your student ID"
                      required
                    />
                  </div>
                </>
              )}
              
              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  placeholder="Enter your email"
                  required
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="password">Password</label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  value={formData.password}
                  onChange={handleInputChange}
                  placeholder="Enter your password"
                  required
                />
              </div>
              
              <button 
                type="submit" 
                className="submit-btn"
                style={{ background: roleColors[selectedRole] }}
              >
                {isSignUp ? 'Sign Up' : 'Login'}
              </button>
            </form>

            {!isSignUp && (
              <div className="forgot-password">
                <button 
                  className="forgot-password-btn"
                  onClick={handleForgotPassword}
                >
                  Forgot Password?
                </button>
              </div>
            )}

            {selectedRole === 'student' && (
              <div className="toggle-form">
                <p>
                  {isSignUp ? 'Already have an account?' : "Don't have an account?"}
                  <button 
                    className="toggle-btn"
                    onClick={() => setIsSignUp(!isSignUp)}
                  >
                    {isSignUp ? 'Login' : 'Sign Up'}
                  </button>
                </p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
