import React, { useState } from 'react';
import './App.css';
import WaveBackground from './WaveBackground';

function App() {
  const [selectedRole, setSelectedRole] = useState('student');
  const [isSignUp, setIsSignUp] = useState(false);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    name: '',
    studentId: ''
  });

  const roleColors = {
    student: 'radial-gradient(ellipse at center, #1a5c1a 0%, #0d3d0d 50%, #051a05 100%)',
    teacher: 'radial-gradient(ellipse at center, #5c1a1a 0%, #3d0d0d 50%, #1a0505 100%)',
    admin: 'radial-gradient(ellipse at center, #1a4d5c 0%, #0d2d3d 50%, #05141a 100%)'
  };

  const handleRoleChange = (role) => {
    setSelectedRole(role);
    setIsSignUp(false);
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isSignUp) {
      console.log('Sign Up Data:', formData);
      alert(`Student Sign Up Successful!\nName: ${formData.name}\nEmail: ${formData.email}`);
    } else {
      console.log('Login Data:', { role: selectedRole, ...formData });
      alert(`Login as ${selectedRole.toUpperCase()}\nEmail: ${formData.email}`);
    }
    // Reset form
    setFormData({ email: '', password: '', name: '', studentId: '' });
  };

  const handleForgotPassword = () => {
    if (formData.email) {
      alert(`Password reset link has been sent to:\n${formData.email}`);
    } else {
      alert('Please enter your email address first to reset your password.');
    }
  };

  return (
    <div className="app" style={{ background: roleColors[selectedRole] }}>
      <div className="wave-background">
        {/* Particles/Sparkles */}
        <div className="particles">
          {[...Array(50)].map((_, i) => (
            <div key={i} className="particle" style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              animationDelay: `${Math.random() * 5}s`,
              animationDuration: `${3 + Math.random() * 4}s`
            }}></div>
          ))}
        </div>
        
        {/* Curved wave lines at bottom */}
        <div className="silk-waves">
          <svg className="wave-curves" viewBox="0 0 1440 250" preserveAspectRatio="none">
            <defs>
              {/* Pure white - brightest */}
              <linearGradient id="wave-grad-bright" x1="0%" y1="0%" x2="100%" y2="0%">
                <stop offset="0%" style={{stopColor: 'rgba(255,255,255,0.2)', stopOpacity: 1}} />
                <stop offset="50%" style={{stopColor: 'rgba(255,255,255,0.7)', stopOpacity: 1}} />
                <stop offset="100%" style={{stopColor: 'rgba(255,255,255,0.2)', stopOpacity: 1}} />
              </linearGradient>
              {/* Light gray-white */}
              <linearGradient id="wave-grad-light" x1="0%" y1="0%" x2="100%" y2="0%">
                <stop offset="0%" style={{stopColor: 'rgba(230,230,230,0.15)', stopOpacity: 1}} />
                <stop offset="50%" style={{stopColor: 'rgba(240,240,240,0.5)', stopOpacity: 1}} />
                <stop offset="100%" style={{stopColor: 'rgba(230,230,230,0.15)', stopOpacity: 1}} />
              </linearGradient>
              {/* Medium gray-white */}
              <linearGradient id="wave-grad-medium" x1="0%" y1="0%" x2="100%" y2="0%">
                <stop offset="0%" style={{stopColor: 'rgba(200,200,200,0.12)', stopOpacity: 1}} />
                <stop offset="50%" style={{stopColor: 'rgba(220,220,220,0.4)', stopOpacity: 1}} />
                <stop offset="100%" style={{stopColor: 'rgba(200,200,200,0.12)', stopOpacity: 1}} />
              </linearGradient>
              {/* Dark gray-white */}
              <linearGradient id="wave-grad-dark" x1="0%" y1="0%" x2="100%" y2="0%">
                <stop offset="0%" style={{stopColor: 'rgba(180,180,180,0.08)', stopOpacity: 1}} />
                <stop offset="50%" style={{stopColor: 'rgba(200,200,200,0.28)', stopOpacity: 1}} />
                <stop offset="100%" style={{stopColor: 'rgba(180,180,180,0.08)', stopOpacity: 1}} />
              </linearGradient>
              <filter id="wave-blur">
                <feGaussianBlur stdDeviation="6" />
              </filter>
            </defs>
            
            {/* Flowing silk-like curves */}
            <path className="wave-line wave-1" fill="none" stroke="url(#wave-grad-bright)" strokeWidth="8" filter="url(#wave-blur)"
              d="M-200,180 C-100,170 -50,190 50,175 C150,160 250,195 350,175 C450,155 550,185 650,170 C750,155 850,180 950,165 C1050,150 1150,175 1250,160 C1350,145 1450,170 1550,155 C1650,140 1750,165 1850,150" />
            
            <path className="wave-line wave-2" fill="none" stroke="url(#wave-grad-bright)" strokeWidth="9" filter="url(#wave-blur)"
              d="M-200,135 C-100,145 -50,125 50,140 C150,155 250,130 350,145 C450,160 550,140 650,155 C750,170 850,150 950,165 C1050,180 1150,160 1250,175 C1350,190 1450,170 1550,185 C1650,200 1750,180 1850,195" />
            
            {/* Light gray-white flowing curves */}
            <path className="wave-line wave-3" fill="none" stroke="url(#wave-grad-light)" strokeWidth="7" filter="url(#wave-blur)"
              d="M-200,115 C-100,125 -50,105 50,120 C150,135 250,115 350,130 C450,145 550,130 650,145 C750,160 850,145 950,160 C1050,175 1150,160 1250,175 C1350,190 1450,175 1550,190 C1650,205 1750,190 1850,205" />
            
            <path className="wave-line wave-4" fill="none" stroke="url(#wave-grad-light)" strokeWidth="7.5" filter="url(#wave-blur)"
              d="M-200,160 C-100,150 -50,170 50,155 C150,140 250,165 350,150 C450,135 550,160 650,145 C750,130 850,155 950,140 C1050,125 1150,150 1250,135 C1350,120 1450,145 1550,130 C1650,115 1750,140 1850,125" />
            
            <path className="wave-line wave-5" fill="none" stroke="url(#wave-grad-light)" strokeWidth="6.5" filter="url(#wave-blur)"
              d="M-200,95 C-100,105 -50,85 50,100 C150,115 250,95 350,110 C450,125 550,110 650,125 C750,140 850,125 950,140 C1050,155 1150,140 1250,155 C1350,170 1450,155 1550,170 C1650,185 1750,170 1850,185" />
            
            {/* Medium gray-white flowing curves */}
            <path className="wave-line wave-6" fill="none" stroke="url(#wave-grad-medium)" strokeWidth="6" filter="url(#wave-blur)"
              d="M-200,108 C-100,118 -50,98 50,113 C150,128 250,108 350,123 C450,138 550,123 650,138 C750,153 850,138 950,153 C1050,168 1150,153 1250,168 C1350,183 1450,168 1550,183 C1650,198 1750,183 1850,198" />
            
            <path className="wave-line wave-7" fill="none" stroke="url(#wave-grad-medium)" strokeWidth="6.8" filter="url(#wave-blur)"
              d="M-200,148 C-100,138 -50,158 50,143 C150,128 250,153 350,138 C450,123 550,148 650,133 C750,118 850,143 950,128 C1050,113 1150,138 1250,123 C1350,108 1450,133 1550,118 C1650,103 1750,128 1850,113" />
            
            <path className="wave-line wave-8" fill="none" stroke="url(#wave-grad-medium)" strokeWidth="7" filter="url(#wave-blur)"
              d="M-200,170 C-100,160 -50,180 50,165 C150,150 250,175 350,160 C450,145 550,170 650,155 C750,140 850,165 950,150 C1050,135 1150,160 1250,145 C1350,130 1450,155 1550,140 C1650,125 1750,150 1850,135" />
            
            {/* Dark gray-white flowing curves */}
            <path className="wave-line wave-9" fill="none" stroke="url(#wave-grad-dark)" strokeWidth="5.5" filter="url(#wave-blur)"
              d="M-200,103 C-100,113 -50,93 50,108 C150,123 250,103 350,118 C450,133 550,118 650,133 C750,148 850,133 950,148 C1050,163 1150,148 1250,163 C1350,178 1450,163 1550,178 C1650,193 1750,178 1850,193" />
            
            <path className="wave-line wave-10" fill="none" stroke="url(#wave-grad-dark)" strokeWidth="6" filter="url(#wave-blur)"
              d="M-200,128 C-100,138 -50,118 50,133 C150,148 250,128 350,143 C450,158 550,143 650,158 C750,173 850,158 950,173 C1050,188 1150,173 1250,188 C1350,203 1450,188 1550,203 C1650,218 1750,203 1850,218" />
            
            <path className="wave-line wave-11" fill="none" stroke="url(#wave-grad-dark)" strokeWidth="6.2" filter="url(#wave-blur)"
              d="M-200,153 C-100,143 -50,163 50,148 C150,133 250,158 350,143 C450,128 550,153 650,138 C750,123 850,148 950,133 C1050,118 1150,143 1250,128 C1350,113 1450,138 1550,123 C1650,108 1750,133 1850,118" />
            
            <path className="wave-line wave-12" fill="none" stroke="url(#wave-grad-dark)" strokeWidth="5.8" filter="url(#wave-blur)"
              d="M-200,188 C-100,178 -50,198 50,183 C150,168 250,193 350,178 C450,163 550,188 650,173 C750,158 850,183 950,168 C1050,153 1150,178 1250,163 C1350,148 1450,173 1550,158 C1650,143 1750,168 1850,153" />
            
            {/* Additional bright flowing curves */}
            <path className="wave-line wave-13" fill="none" stroke="url(#wave-grad-bright)" strokeWidth="7.5" filter="url(#wave-blur)"
              d="M-200,83 C-100,93 -50,73 50,88 C150,103 250,83 350,98 C450,113 550,98 650,113 C750,128 850,113 950,128 C1050,143 1150,128 1250,143 C1350,158 1450,143 1550,158 C1650,173 1750,158 1850,173" />
          </svg>
        </div>
      </div>
      <div className="main-title">
        <div className="title-icon">ðŸ“š</div>
        <div className="title-text-large">Library Duty</div>
        <div className="title-text-small">Assignment System</div>
      </div>
      
      <div className="login-container">
        <div className="login-box">
          {!isSignUp && (
            <div className="role-selector">
              <button
                className={`role-btn ${selectedRole === 'student' ? 'active' : ''}`}
                onClick={() => handleRoleChange('student')}
                style={{ 
                  background: selectedRole === 'student' ? roleColors.student : '#f0f0f0',
                  color: selectedRole === 'student' ? 'white' : '#333'
                }}
              >
                Student
              </button>
              <button
                className={`role-btn ${selectedRole === 'teacher' ? 'active' : ''}`}
                onClick={() => handleRoleChange('teacher')}
                style={{ 
                  background: selectedRole === 'teacher' ? roleColors.teacher : '#f0f0f0',
                  color: selectedRole === 'teacher' ? 'white' : '#333'
                }}
              >
                Teacher
              </button>
              <button
                className={`role-btn ${selectedRole === 'admin' ? 'active' : ''}`}
                onClick={() => handleRoleChange('admin')}
                style={{ 
                  background: selectedRole === 'admin' ? roleColors.admin : '#f0f0f0',
                  color: selectedRole === 'admin' ? 'white' : '#333'
                }}
              >
                Admin
              </button>
            </div>
          )}

          <div className="form-container">
            <h2 className="form-title">
              {isSignUp ? 'Student Sign Up' : `${selectedRole.charAt(0).toUpperCase() + selectedRole.slice(1)} Login`}
            </h2>
            
            <form onSubmit={handleSubmit}>
              {isSignUp && (
                <>
                  <div className="form-group">
                    <label htmlFor="name">Full Name</label>
                    <input
                      type="text"
                      id="name"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      placeholder="Enter your full name"
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="studentId">Student ID</label>
                    <input
                      type="text"
                      id="studentId"
                      name="studentId"
                      value={formData.studentId}
                      onChange={handleInputChange}
                      placeholder="Enter your student ID"
                      required
                    />
                  </div>
                </>
              )}
              
              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  placeholder="Enter your email"
                  required
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="password">Password</label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  value={formData.password}
                  onChange={handleInputChange}
                  placeholder="Enter your password"
                  required
                />
              </div>
              
              <button 
                type="submit" 
                className="submit-btn"
                style={{ background: roleColors[selectedRole] }}
              >
                {isSignUp ? 'Sign Up' : 'Login'}
              </button>
            </form>

            {!isSignUp && (
              <div className="forgot-password">
                <button 
                  className="forgot-password-btn"
                  onClick={handleForgotPassword}
                >
                  Forgot Password?
                </button>
              </div>
            )}

            {selectedRole === 'student' && (
              <div className="toggle-form">
                <p>
                  {isSignUp ? 'Already have an account?' : "Don't have an account?"}
                  <button 
                    className="toggle-btn"
                    onClick={() => setIsSignUp(!isSignUp)}
                  >
                    {isSignUp ? 'Login' : 'Sign Up'}
                  </button>
                </p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
