import { useEffect, useRef } from 'react';
import * as THREE from 'three';

const WaveBackground = () => {
  const containerRef = useRef(null);

  useEffect(() => {
    if (!containerRef.current) return;

    // Scene setup
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(
      60,
      window.innerWidth / (window.innerHeight * 0.45),
      0.1,
      1000
    );
    camera.position.set(0, 2, 8);
    camera.lookAt(0, 0, 0);

    const renderer = new THREE.WebGLRenderer({ 
      alpha: true, 
      antialias: true 
    });
    const height = window.innerHeight * 0.45;
    renderer.setSize(window.innerWidth, height);
    renderer.setClearColor(0x000000, 0);
    containerRef.current.appendChild(renderer.domElement);

    // Create multiple wave planes with different materials
    const waves = [];
    const waveCount = 8;

    for (let i = 0; i < waveCount; i++) {
      const geometry = new THREE.PlaneGeometry(15, 3, 100, 20);
      
      // Different shades of white/gray
      const colors = [
        0xffffff, // pure white
        0xf0f0f0, // light gray
        0xe0e0e0, // medium-light gray
        0xd0d0d0, // medium gray
        0xffffff, // pure white
        0xf5f5f5, // off-white
        0xe5e5e5, // light-medium gray
        0xd5d5d5  // medium-dark gray
      ];

      const material = new THREE.MeshPhongMaterial({
        color: colors[i],
        transparent: true,
        opacity: 0.3 + (i * 0.05),
        side: THREE.DoubleSide,
        shininess: 100,
        specular: 0xffffff
      });

      const mesh = new THREE.Mesh(geometry, material);
      
      // Position waves at different depths and heights
      mesh.position.z = -i * 0.3;
      mesh.position.y = -1 + (i * 0.15);
      mesh.rotation.x = Math.PI / 6;
      
      waves.push({
        mesh,
        geometry,
        speed: 0.0005 + i * 0.0002,
        amplitude: 0.15 + i * 0.02,
        frequency: 0.5 + i * 0.1
      });
      
      scene.add(mesh);
    }

    // Lighting
    const ambientLight = new THREE.AmbientLight(0xffffff, 0.8);
    scene.add(ambientLight);

    const directionalLight = new THREE.DirectionalLight(0xffffff, 0.6);
    directionalLight.position.set(0, 1, 1);
    scene.add(directionalLight);

    const pointLight = new THREE.PointLight(0xffffff, 0.5);
    pointLight.position.set(0, 2, 3);
    scene.add(pointLight);

    // Animation
    let time = 0;
    const animate = () => {
      requestAnimationFrame(animate);
      time += 0.01;

      waves.forEach((wave, index) => {
        const positions = wave.geometry.attributes.position;
        
        for (let i = 0; i < positions.count; i++) {
          const x = positions.getX(i);
          const y = positions.getY(i);
          
          // Create flowing wave motion
          const waveX = Math.sin(x * wave.frequency + time * wave.speed * 10) * wave.amplitude;
          const waveY = Math.cos(y * wave.frequency * 0.5 + time * wave.speed * 8) * wave.amplitude * 0.5;
          
          positions.setZ(i, waveX + waveY);
        }
        
        positions.needsUpdate = true;
        
        // Gentle rotation and movement
        wave.mesh.rotation.y = Math.sin(time * wave.speed * 5) * 0.1;
        wave.mesh.position.x = Math.sin(time * wave.speed * 3) * 0.3;
      });

      renderer.render(scene, camera);
    };

    animate();

    // Handle resize
    const handleResize = () => {
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    };
    window.addEventListener('resize', handleResize);

    // Cleanup
    return () => {
      window.removeEventListener('resize', handleResize);
      if (containerRef.current && renderer.domElement) {
        containerRef.current.removeChild(renderer.domElement);
      }
      waves.forEach(wave => {
        wave.geometry.dispose();
        wave.mesh.material.dispose();
      });
      renderer.dispose();
    };
  }, []);

  return (
    <div
      ref={containerRef}
      style={{
        position: 'absolute',
        top: '55%',
        left: 0,
        width: '100%',
        height: '45%',
        zIndex: 0,
        pointerEvents: 'none',
        overflow: 'hidden'
      }}
    />
  );
};

export default WaveBackground;
