import React, { useState } from 'react';
import './App.css';

function App() {
  const [selectedRole, setSelectedRole] = useState('student');
  const [isSignUp, setIsSignUp] = useState(false);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    name: '',
    studentId: ''
  });

  const roleColors = {
    student: 'linear-gradient(135deg, #0f9b0f 0%, #00bf63 50%, #00d084 100%)',
    teacher: 'linear-gradient(135deg, #c31432 0%, #e85d75 50%, #ff6b9d 100%)',
    admin: 'linear-gradient(135deg, #134e5e 0%, #0891b2 50%, #22d3ee 100%)'
  };

  const handleRoleChange = (role) => {
    setSelectedRole(role);
    setIsSignUp(false);
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isSignUp) {
      console.log('Sign Up Data:', formData);
      alert(`Student Sign Up Successful!\nName: ${formData.name}\nEmail: ${formData.email}`);
    } else {
      console.log('Login Data:', { role: selectedRole, ...formData });
      alert(`Login as ${selectedRole.toUpperCase()}\nEmail: ${formData.email}`);
    }
    // Reset form
    setFormData({ email: '', password: '', name: '', studentId: '' });
  };

  const handleForgotPassword = () => {
    if (formData.email) {
      alert(`Password reset link has been sent to:\n${formData.email}`);
    } else {
      alert('Please enter your email address first to reset your password.');
    }
  };

  return (
    <div className="app" style={{ background: roleColors[selectedRole] }}>
      <div className="wave-background">
        <svg className="waves" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1440 800" preserveAspectRatio="none">
          <defs>
            <linearGradient id="wave-gradient-1" x1="0%" y1="0%" x2="100%" y2="0%">
              <stop offset="0%" style={{stopColor: 'rgba(255,255,255,0.15)', stopOpacity: 1}} />
              <stop offset="50%" style={{stopColor: 'rgba(255,255,255,0.4)', stopOpacity: 1}} />
              <stop offset="100%" style={{stopColor: 'rgba(255,255,255,0.15)', stopOpacity: 1}} />
            </linearGradient>
            <linearGradient id="wave-gradient-2" x1="0%" y1="0%" x2="100%" y2="0%">
              <stop offset="0%" style={{stopColor: 'rgba(255,255,255,0.1)', stopOpacity: 1}} />
              <stop offset="50%" style={{stopColor: 'rgba(255,255,255,0.35)', stopOpacity: 1}} />
              <stop offset="100%" style={{stopColor: 'rgba(255,255,255,0.1)', stopOpacity: 1}} />
            </linearGradient>
            <filter id="glow-strong">
              <feGaussianBlur stdDeviation="12" result="coloredBlur"/>
              <feMerge>
                <feMergeNode in="coloredBlur"/>
                <feMergeNode in="coloredBlur"/>
                <feMergeNode in="SourceGraphic"/>
              </feMerge>
            </filter>
            <filter id="glow-medium">
              <feGaussianBlur stdDeviation="8" result="coloredBlur"/>
              <feMerge>
                <feMergeNode in="coloredBlur"/>
                <feMergeNode in="SourceGraphic"/>
              </feMerge>
            </filter>
          </defs>
          
          {/* Complex flowing waves with multiple curves */}
          <path className="wave-path wave1" fill="none" stroke="url(#wave-gradient-1)" strokeWidth="8" 
            strokeLinecap="round" filter="url(#glow-strong)"
            d="M-200,350 C100,250 200,450 500,350 C800,250 900,450 1200,350 C1500,250 1600,450 1900,350" />
          
          <path className="wave-path wave2" fill="none" stroke="url(#wave-gradient-2)" strokeWidth="6" 
            strokeLinecap="round" filter="url(#glow-strong)"
            d="M-200,280 C50,380 250,180 500,280 C750,380 950,180 1200,280 C1450,380 1650,180 1900,280" />
          
          <path className="wave-path wave3" fill="none" stroke="url(#wave-gradient-1)" strokeWidth="7" 
            strokeLinecap="round" filter="url(#glow-medium)"
            d="M-200,450 C150,350 300,550 600,450 C900,350 1000,550 1300,450 C1600,350 1700,550 2000,450" />
          
          <path className="wave-path wave4" fill="none" stroke="url(#wave-gradient-2)" strokeWidth="5" 
            strokeLinecap="round" filter="url(#glow-medium)"
            d="M-200,520 C100,420 300,620 600,520 C900,420 1100,620 1400,520 C1700,420 1900,620 2200,520" />
            
          <path className="wave-path wave5" fill="none" stroke="url(#wave-gradient-1)" strokeWidth="6" 
            strokeLinecap="round" filter="url(#glow-medium)"
            d="M-200,200 C50,300 200,100 500,200 C800,300 950,100 1250,200 C1550,300 1700,100 2000,200" />
            
          <path className="wave-path wave6" fill="none" stroke="rgba(255,255,255,0.25)" strokeWidth="4" 
            strokeLinecap="round" filter="url(#glow-medium)"
            d="M-200,380 C150,480 350,280 650,380 C950,480 1150,280 1450,380 C1750,480 1950,280 2250,380" />
        </svg>
      </div>
      <div className="main-title">
        <div className="title-icon">ðŸ“š</div>
        <div className="title-text-large">Library Duty</div>
        <div className="title-text-small">Assignment System</div>
      </div>
      
      <div className="login-container">
        <div className="login-box">
          {!isSignUp && (
            <div className="role-selector">
              <button
                className={`role-btn ${selectedRole === 'student' ? 'active' : ''}`}
                onClick={() => handleRoleChange('student')}
                style={{ 
                  background: selectedRole === 'student' ? roleColors.student : '#f0f0f0',
                  color: selectedRole === 'student' ? 'white' : '#333'
                }}
              >
                Student
              </button>
              <button
                className={`role-btn ${selectedRole === 'teacher' ? 'active' : ''}`}
                onClick={() => handleRoleChange('teacher')}
                style={{ 
                  background: selectedRole === 'teacher' ? roleColors.teacher : '#f0f0f0',
                  color: selectedRole === 'teacher' ? 'white' : '#333'
                }}
              >
                Teacher
              </button>
              <button
                className={`role-btn ${selectedRole === 'admin' ? 'active' : ''}`}
                onClick={() => handleRoleChange('admin')}
                style={{ 
                  background: selectedRole === 'admin' ? roleColors.admin : '#f0f0f0',
                  color: selectedRole === 'admin' ? 'white' : '#333'
                }}
              >
                Admin
              </button>
            </div>
          )}

          <div className="form-container">
            <h2 className="form-title">
              {isSignUp ? 'Student Sign Up' : `${selectedRole.charAt(0).toUpperCase() + selectedRole.slice(1)} Login`}
            </h2>
            
            <form onSubmit={handleSubmit}>
              {isSignUp && (
                <>
                  <div className="form-group">
                    <label htmlFor="name">Full Name</label>
                    <input
                      type="text"
                      id="name"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      placeholder="Enter your full name"
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="studentId">Student ID</label>
                    <input
                      type="text"
                      id="studentId"
                      name="studentId"
                      value={formData.studentId}
                      onChange={handleInputChange}
                      placeholder="Enter your student ID"
                      required
                    />
                  </div>
                </>
              )}
              
              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  placeholder="Enter your email"
                  required
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="password">Password</label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  value={formData.password}
                  onChange={handleInputChange}
                  placeholder="Enter your password"
                  required
                />
              </div>
              
              <button 
                type="submit" 
                className="submit-btn"
                style={{ background: roleColors[selectedRole] }}
              >
                {isSignUp ? 'Sign Up' : 'Login'}
              </button>
            </form>

            {!isSignUp && (
              <div className="forgot-password">
                <button 
                  className="forgot-password-btn"
                  onClick={handleForgotPassword}
                >
                  Forgot Password?
                </button>
              </div>
            )}

            {selectedRole === 'student' && (
              <div className="toggle-form">
                <p>
                  {isSignUp ? 'Already have an account?' : "Don't have an account?"}
                  <button 
                    className="toggle-btn"
                    onClick={() => setIsSignUp(!isSignUp)}
                  >
                    {isSignUp ? 'Login' : 'Sign Up'}
                  </button>
                </p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
