import { useEffect, useRef } from 'react';
import * as THREE from 'three';

const WaveBackground = () => {
  const containerRef = useRef(null);

  useEffect(() => {
    const container = containerRef.current;
    if (!container) return;

    // Scene setup
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(
      75,
      window.innerWidth / (window.innerHeight * 0.5),
      0.1,
      1000
    );
    camera.position.set(0, 4, 12);
    camera.lookAt(0, -2, 0);

    const renderer = new THREE.WebGLRenderer({ 
      alpha: true, 
      antialias: true,
      powerPreference: "high-performance"
    });
    const height = window.innerHeight * 0.5;
    renderer.setSize(window.innerWidth, height);
    renderer.setClearColor(0x000000, 0);
    container.appendChild(renderer.domElement);

    // Create flowing 3D wave ribbons
    const waves = [];
    const waveCount = 6;

    for (let i = 0; i < waveCount; i++) {
      // Create flowing ribbon geometry
      const geometry = new THREE.PlaneGeometry(30, 4, 200, 40);
      
      // Different shades for depth
      const brightness = 1 - (i * 0.15);
      const color = new THREE.Color(brightness, brightness, brightness);

      const material = new THREE.MeshPhongMaterial({
        color: color,
        transparent: true,
        opacity: 0.6 - (i * 0.08),
        side: THREE.DoubleSide,
        shininess: 150,
        specular: new THREE.Color(1, 1, 1),
        emissive: color,
        emissiveIntensity: 0.3 + (i * 0.05),
        flatShading: false
      });

      const mesh = new THREE.Mesh(geometry, material);
      
      // Position waves with clear depth separation
      mesh.position.z = -i * 1.5;
      mesh.position.y = -2 + (i * 0.3);
      mesh.rotation.x = Math.PI / 3.5;
      
      waves.push({
        mesh,
        geometry,
        speed: 0.5 + i * 0.15,
        amplitude: 0.4 + i * 0.1,
        frequency: 1.5 - i * 0.15,
        offset: i * Math.PI / 3,
        depthOffset: i * 0.5
      });
      
      scene.add(mesh);
    }

    // Dramatic lighting for 3D effect
    const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
    scene.add(ambientLight);

    const mainLight = new THREE.DirectionalLight(0xffffff, 1.2);
    mainLight.position.set(0, 8, 10);
    scene.add(mainLight);

    const backLight = new THREE.DirectionalLight(0x88ccff, 0.8);
    backLight.position.set(0, -5, -5);
    scene.add(backLight);

    const rimLight1 = new THREE.PointLight(0xffffff, 1.5);
    rimLight1.position.set(-10, 3, 5);
    scene.add(rimLight1);

    const rimLight2 = new THREE.PointLight(0xffffff, 1.5);
    rimLight2.position.set(10, 3, 5);
    scene.add(rimLight2);

    // Animation
    let time = 0;
    const animate = () => {
      requestAnimationFrame(animate);
      time += 0.01;

      waves.forEach((wave, index) => {
        const positions = wave.geometry.attributes.position;
        
        for (let i = 0; i < positions.count; i++) {
          const x = positions.getX(i);
          const y = positions.getY(i);
          
          // Create flowing wave motion with more amplitude
          const waveX = Math.sin(x * wave.frequency + time * wave.speed * 12 + wave.offset) * wave.amplitude;
          const waveY = Math.cos(y * wave.frequency * 0.6 + time * wave.speed * 10 + wave.offset) * wave.amplitude * 0.6;
          const waveZ = Math.sin((x + y) * wave.frequency * 0.3 + time * wave.speed * 8) * wave.amplitude * 0.3;
          
          positions.setZ(i, waveX + waveY + waveZ);
        }
        
        positions.needsUpdate = true;
        
        // Gentle rotation and movement for organic flow
        wave.mesh.rotation.y = Math.sin(time * wave.speed * 6 + wave.offset) * 0.15;
        wave.mesh.position.x = Math.sin(index * 0.5) * 2 + Math.sin(time * wave.speed * 4 + wave.offset) * 0.5;
        wave.mesh.position.y = -0.5 + (index * 0.2) - Math.sin(index) * 0.3 + Math.cos(time * wave.speed * 3 + wave.offset) * 0.2;
      });

      renderer.render(scene, camera);
    };

    animate();

    // Handle resize
    const handleResize = () => {
      const height = window.innerHeight * 0.45;
      camera.aspect = window.innerWidth / height;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, height);
    };
    window.addEventListener('resize', handleResize);

    // Cleanup
    return () => {
      window.removeEventListener('resize', handleResize);
      if (container && renderer.domElement) {
        container.removeChild(renderer.domElement);
      }
      waves.forEach(wave => {
        wave.geometry.dispose();
        wave.mesh.material.dispose();
      });
      renderer.dispose();
    };
  }, []);

  return (
    <div
      ref={containerRef}
      style={{
        position: 'absolute',
        top: '55%',
        left: 0,
        width: '100%',
        height: '45%',
        zIndex: 0,
        pointerEvents: 'none',
        overflow: 'hidden'
      }}
    />
  );
};

export default WaveBackground;
